import React from 'react';
import { Container, Form, Jumbotron, Button } from 'react-bootstrap';
import NavBa from './NavBar';
//import Input from './input'

function Input(props) {
    const [value, setValue] = useState(props.value);
    
    function onChange(event) {
        console.log(event.target.value);
        if (props.onChange) {
            props.onChange(props.id, event.target.value)
        }
        event.preventDefault();
        setValue(event.target.value);
    }
    
    const id = props.id;
    return (
        <div className='form-group'>
            <label htmlFor='inputText'>{props.label}</label>
            <input className='form-control' id={id + 'inputText'} type='text' value={value} onChange={onChange} placeholder={props.placeholder}/>
            <small id={id + 'value'} className='form-text text-muted'>{value}</small>
        </div>
    );
}

const Podmenu = () => {
    return (
        <Form inline>
                <Form.Label className="mb-2 mr-sm-2" htmlFor="inlineFormCustomSelectPref">
                    Název dotazníku</Form.Label>
                <Form.Control 
                placeholder="Název"
                type="text"
                name="SName"
                className="mb-2 mr-sm-2"
                required />

                <Form.Label className="mb-2 mr-sm-2" htmlFor="inlineFormCustomSelectPref">
                    Autor dotazníku</Form.Label>
                <Form.Control 
                placeholder="Autor"
                type="text"
                name="SAuthor"
                className="mb-2 mr-sm-2" 
                required />

                <Input label='Jméno' value={formData['name']} id='name' placeholder='Zadejte jméno' onChange={onChange}></Input>

                <input 
                type="submit"
                value="Odeslat"
                name="submit" />
        </Form>
    )
}

const CreateS = () => (
    <Container className="p-1">
        <NavBa></NavBa>
        <Jumbotron>
            <h2>Vytvořit anketu</h2>
            <p>Nástroj pro tvorbu interaktvních anket se sběrem dat</p>
        </Jumbotron>
        <Podmenu />
    </Container>
);

export default CreateS;
