import React from 'react';
import NavBa from './NavBar';
import { Card, CardColumns, Container, Jumbotron, ListGroup, ListGroupItem } from 'react-bootstrap';
import JSONdata from './AlbumData'

const Survey = (props) => {
    return (
        <Card>
            <Card.Body>
                <Card.Title><h3>{props.data.info[0].SName ? props.data.info[0].SName : "defName"}</h3></Card.Title>
                <Card.Subtitle className="mb-2 text-muted">Autor: {props.SAuthor ? props.SAuthor : "defAuth"}</Card.Subtitle>
                <Card.Text>
                    {props.SAbout ? props.SAbout : "defAbout"}
                </Card.Text>
            </Card.Body>
            <ListGroup className="list-group-flush">
                <ListGroupItem>Počet otázek: {props.QuestSum ? props.QuestSum : "0"}</ListGroupItem>
                <ListGroupItem><b>Doba:</b> {props.STime ? props.STime : "0 minut"}</ListGroupItem>
            </ListGroup>
            <Card.Body>
                <Card.Link href=""><b>Spustit</b></Card.Link>
                <Card.Link href="">Smazat</Card.Link>
            </Card.Body>
        </Card>
    )
}

const Podmenu = (props) => {
    const Result=[]
    console.log(JSON.stringify(props.JSONdata));

    for(const item of props.JSONdata) {
        Result.push(<Survey data={item}></Survey>);
    }
    return (
        <CardColumns>{Result}</CardColumns>
    )
}

const Album = () => (
    <Container className="p-1">
        <NavBa></NavBa>
        <Jumbotron>
            <h2>Prohlížení zveřejněných anket</h2><br></br>
            <p>Níže vyberte požadovanou anketu, kterou zahájíte příslušným tlačítkem</p>
        </Jumbotron>
        <Podmenu JSONdata={JSONdata}/>
    </Container>
);

export default Album;
